  crearFactura(response, response2) {
      let fecha = response[0].fecha.split(" ");
    	let fecha1 = fecha[0].split("-");
    	let fecha2 = fecha[1].split(":");
    	let total = 0;

      for(let i=0; i<response2.length; i++) {
        total = total + parseFloat(response2[i].importe);
      }

      console.log(fecha1);
      console.log(fecha2);
      console.log(total);

      var content: any[] = [
					{
						image: 'data:image/png;base64,',
						width: 140,
						height: 100,
					},
					{ text: '\nResumen factura', style: 'header' },
							'Compra realizada por '+response[0].nombre+', con DNI '+response[0].dni,
							'Compra realizada el '+fecha1[2]+' del '+fecha1[1]+' del '+fecha1[0]+' a las '+fecha2[0]+':'+fecha2[1]+' horas.',
							{ text: 'Resumen compra:', style: 'subheader' },
							{
							    style: 'tableExample',
									table: {
											headerRows: 1,
											body: [
													[{ text: 'Cantidad', style: 'tableHeader' }, { text: 'Producto', style: 'tableHeader'}, { text: 'Precio unidad', style: 'tableHeader' }, { text: 'Importe', style: 'tableHeader'}],
											]
									},
									layout: 'lightHorizontalLines'
							},
					
					
				];

      var dd = {
				content,		
				images: {
				},
				styles: {
					header: {
						fontSize: 18,
						bold: true,
						margin: [0, 0, 0, 10]
					},
					subheader: {
						fontSize: 16,
						bold: true,
						margin: [0, 10, 0, 5]
					},
					tableExample: {
						margin: [0, 5, 0, 15]
					},
					tableHeader: {
						bold: true,
						fontSize: 13,
						color: 'black'
					}
				},
				defaultStyle: {
					// alignment: 'justify'
				}		
			};
      console.log(dd.content[5].table);
      var lines = response2.length;
    	for(var j=0; j<response2.length; j++) {
    		dd.content[5].table.body[j+1] = [response2[j].cantidad.toString(),response2[j].nombre, response2[j].precio.toString(), response2[j].importe.toString()];
    	}
    	dd.content[5].table.body[lines+1] = ['Subtotal','','',total.toString()];
    	dd.content[5].table.body[lines+2] = ['Descuento','','','-'+total.toString()];
    	dd.content[5].table.body[lines+3] = ['TOTAL','','','0.00 euros'];

      pdfMake.createPdf(dd).download();
  }

